import React, { useState} from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import "bootstrap/dist/css/bootstrap.min.css";
import "react-datepicker/dist/react-datepicker.css";
import "./Calendar.css"; // Custom CSS for calendar styling

function Calendar() {


  const [date, setDate] = useState(new Date());

  const handleDateChange = (newDate) => {
    setDate(newDate);
    // Handle date selection, e.g., fetch events for the selected date
  };

  return (
    <div className="full-calendar-page">
    <h2>Full-Size Calendar</h2>
    <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        // Add more options and event handlers as needed
      />
  </div>
  );
}

export default Calendar;
