import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import './Calendar.css';

function Calendar() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [emails, setEmails] = useState('');
  const [events, setEvents] = useState([]);
  // Fetch events based on the selected date range
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/events?start_date=${startDate.toISOString().split('T')[0]}&end_date=${endDate.toISOString().split('T')[0]}`);
        if (response.ok) {
          const eventsData = await response.json();
          setEvents(eventsData);
        } else {
          console.error('Failed to fetch events');
        }
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };
    fetchEvents();
  }, [startDate, endDate]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const eventData = {
      title,
      description,
      start_date: startDate.toISOString().split('T')[0],
      end_date: endDate.toISOString().split('T')[0],
      emails
    };

    try {
      const response = await fetch('http://127.0.0.1:5000/api/events', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(eventData),
      });

      if (response.ok) {
        alert('Event created successfully!');
        setTitle('');
        setDescription('');
        setStartDate(new Date());
        setEndDate(new Date());
        setEmails('');
        // Re-fetch events to include the newly created event
        const fetchEvents = async () => {
          try {
            const response = await fetch(`http://127.0.0.1:5000/api/events?start_date=${startDate.toISOString().split('T')[0]}&end_date=${endDate.toISOString().split('T')[0]}`);
            if (response.ok) {
              const eventsData = await response.json();
              setEvents(eventsData);
            } else {
              console.error('Failed to fetch events');
            }
          } catch (error) {
            console.error('Error fetching events:', error);
          }
        };
        fetchEvents();
      } else {
        const errorData = await response.json();
        alert(`Failed to create event: ${errorData.error}`);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };


  return (
    <div className="full-calendar-page">
    <div className="calendar-container">
      <h2 className="calendar-title">Monthly Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        height="auto"
        events={events.map(event => ({
          title: event.title,
          start: event.start_date,
          end: event.end_date,
        }))}
      />
    </div>
    <div className="event-list-container">
      <h2 className="event-list-title">Event List</h2>
      {events.map(event => (
        <div key={event.id} className="event-list-item">
          <div className="event-list-item-title">{event.title}</div>
          <div className="event-list-item-date">
            {new Date(event.start_date).toLocaleDateString()} - {new Date(event.end_date).toLocaleDateString()}
          </div>
          <div className="event-list-item-description">{event.description}</div>
          <div className="app-container">
      <div className="calendar-container">
        <Calendar
          // Customize your calendar here
        />
      </div>
      <div className="event-list-container">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="Title">Title</label>
            <input
              type="text"
              className="form-control"
              id="Title"
              placeholder="Enter Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="Desc">Description</label>
            <input
              type="text"
              className="form-control"
              id="Desc"
              placeholder="Enter Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="StartDate">Start Date</label>
            <DatePicker
              selected={startDate}
              onChange={(date) => setStartDate(date)}
              dateFormat="yyyy-MM-dd"
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="EndDate">End Date</label>
            <DatePicker
              selected={endDate}
              onChange={(date) => setEndDate(date)}
              dateFormat="yyyy-MM-dd"
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="Emails">Emails (comma separated)</label>
            <input
              type="text"
              className="form-control"
              id="Emails"
              value={emails}
              onChange={(e) => setEmails(e.target.value)}
              placeholder="Enter email addresses separated by commas"
            />
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>

        <div className="event-list">
          <h2>Events</h2>
          <ul>
            {events.map((event) => (
              <li key={event.id}>
                <strong>{event.title}</strong><br />
                {event.description}<br />
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
        </div>
      ))}
    </div>
  </div>
  );
}

export default Calendar;
