import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import './Calendar.css';

function Calendar() {
  const [events, setEvents] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);

  useEffect(() => {
    fetchEvents();
    fetchHolidays();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/events');
      const data = await response.json();
      setEvents(data);
      // Initialize the event list with events of today
      filterEvents(new Date().toISOString().split('T')[0]);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleDateClick = (arg) => {
    const clickedDate = arg.dateStr;
    setSelectedDate(clickedDate);
    filterEvents(clickedDate);
  };

  const filterEvents = (date) => {
    const filtered = events.filter(event => 
      new Date(event.start_date).toISOString().split('T')[0] === date ||
      new Date(event.end_date).toISOString().split('T')[0] === date
    );
    setFilteredEvents(filtered);
  };

  return (
    <div className="full-calendar-page">
      <div className="calendar-container">
        <h2 className="calendar-title">Monthly Calendar</h2>
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          height="auto"
          aspectRatio={1.25}
          events={events.map(event => ({
            title: event.title,
            start: event.start_date,
            end: event.end_date,
          }))}
          //eventContent={renderEventContent}
          dateClick={handleDateClick}
        />
      </div>
      <div className="event-list-container">
        <h2 className="event-list-title">Event List</h2>
        {(selectedDate || events.length > 0) && (
          <div>
            <h3>Events on {selectedDate ? new Date(selectedDate).toLocaleDateString() : 'today'}</h3>
            {filteredEvents.length > 0 ? (
              filteredEvents.map(event => (
                <div key={event.id} className="event-list-item">
                  <div className="event-list-item-title">{event.title}</div>
                  <div className="event-list-item-date">
                    {new Date(event.start_date).toLocaleDateString()} - {new Date(event.end_date).toLocaleDateString()}
                  </div>
                  <div className="event-list-item-description">{event.description}</div>
                </div>
              ))
            ) : (
              <p>No events on this date</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

// const renderEventContent = (eventInfo) => {
//   // Return an empty fragment to hide event details
//   return <></>;
// };

export default Calendar;
