import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import './Calendar.css';

function Calendar() {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [filteredEvents, setFilteredEvents] = useState([]);

  useEffect(() => {
    const today = new Date().toISOString().split('T')[0]; // Get today's date in YYYY-MM-DD format
    fetchEvents(today, today); // Fetch events for today initially
  }, []);

  const fetchEvents = async (startDate = null, endDate = null) => {
    try {
      let url = 'http://127.0.0.1:5000/api/events';
      if (startDate && endDate) {
        url += `?start_date=${startDate}&end_date=${endDate}`;
      }
      const response = await fetch(url);
      const data = await response.json();
      setEvents(data);
      if (startDate && endDate) {
        filterEvents(startDate);
      }
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleDateClick = (arg) => {
    const selectedDate = arg.dateStr;
    setSelectedDate(selectedDate);
    fetchEvents(selectedDate, selectedDate); // Fetch events for the selected date
  };

  const filterEvents = (date) => {
    const filtered = events.filter(event => {
      const eventStartDate = new Date(event.start_date).toLocaleDateString();
      const eventEndDate = new Date(event.end_date).toLocaleDateString();
      const selected = new Date(date).toLocaleDateString();
      return (eventStartDate <= selected && eventEndDate >= selected);
    });
    setFilteredEvents(filtered);
  };

  return (
    <div className="full-calendar-page">
      <div className="calendar-container">
        <h2 className="calendar-title">Monthly Calendar</h2>
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          height="auto"
          aspectRatio={1.25} // Adjust aspect ratio for better control
          events={events.map(event => ({
          }))}
          dateClick={handleDateClick}
        />
      </div>
      <div className="event-list-container">
        <h2 className="event-list-title">Event List</h2>
        {(selectedDate || events.length > 0) && (
          <div>
            <h3>Events on {selectedDate ? new Date(selectedDate).toLocaleDateString() : 'today'}</h3>
            {filteredEvents.length > 0 ? (
              filteredEvents.map(event => (
                <div key={event.id} className="event-list-item">
                  <div className="event-list-item-title">{event.title}</div>
                  <div className="event-list-item-date">
                    {new Date(event.start_date).toLocaleDateString()} - {new Date(event.end_date).toLocaleDateString()}
                  </div>
                  <div className="event-list-item-description">{event.description}</div>
                </div>
              ))
            ) : (
              <p>No events on this date</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Calendar;
