function App(){

  const today = new Date();
  const startOfISOWeekDate = startOfISOWeek(today);
  const startOfWeekDate = startOfWeek(today);

  const [text, setText]=useState('')
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());


const onChangeHandler=(event)=>{
  console.log(event.target.name, event.target.value)
  setText(event.target.value)
}

return(
        <div className='App'>
          <form>
            <div class="form-group">
              <label for="Title">Title</label>
              <input type="Title" class="form-control" id="Title" placeholder="Enter Title"/>
            </div>
            <div class="form-group">
              <label for="Desc">Description</label>
              <input type="Desc" class="form-control" id="Desc"  placeholder="Enter Description"/>
            </div>
            <div class="form-group">
              <label for="StartDate">StartDate</label>
                <DatePicker
                  selected={startDate}
                  onChange={(date) => setStartDate(date)}
                  dateFormat="yyyy-MM-dd"
                  className="form-control"
                />
            </div>
            <div class="form-group">
              <label for="exampleInputEmail1">EndDate</label>   
              <DatePicker
                selected={endDate}
                onChange={(date) => setEndDate(date)}
                dateFormat="yyyy-MM-dd"
                className="form-control"
              />
            </div>
            <div class="form-check">
              <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
              <label class="form-check-label" for="exampleCheck1">Check me out</label>
            </div>
            <button type="submit" class="btn btn-primary">Submit</button>
          </form>
        </div>
      );
}

export default App;
