import React, { useState, useEffect } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./Calendar.css"; // Custom CSS for calendar styling

function Calendar() {
  const [startDate, setStartDate] = useState(new Date());
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Fetch events from your API or use static data for demonstration
    fetch('http://127.0.0.1:5000/api/events')
      .then(response => response.json())
      .then(data => setEvents(data))
      .catch(error => console.error('Error fetching events:', error));
  }, []);

  return (
    <div className='container mt-5'>
      <div className='row justify-content-center'>
        <div className='col-md-10'>
          <h2 className='text-center mb-4'>Calendar</h2>
          <div className='calendar-card card shadow-sm'>
            <div className='card-body'>
              <div className="form-group">
                <label htmlFor="StartDate">Select Date</label>
                <DatePicker
                  selected={startDate}
                  onChange={(date) => setStartDate(date)}
                  dateFormat="yyyy-MM-dd"
                  className="form-control"
                />
              </div>
              <div className="events mt-4">
                <h4>Events:</h4>
                <ul className="list-group">
                  {events.map((event, index) => (
                    <li key={index} className="list-group-item">
                      <strong>{event.title}</strong> - {event.start_date} to {event.end_date}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Calendar;
